import React from 'react';
import './App.css';
import axios from 'axios';
import CommentSingle from './Comment/CommentSingle';
import CommentForm from './Comment/CommentForm';
import CommentList from './Comment/CommentList';
import { response } from 'express';

class App extends React.Component() {

  constructor(props) {
    super(props);
    this.state = {
      comments: [],
      currentComment: {},
    }
    this.updateCurrentComment = this.updateCurrentComment.bind(this);
  }

  componentDidMount() {
    const url = 'http://localhost:4000/comments';
    axios.get(url)
      .then((Response) => {
        this.setState({
          comments: Response.data
        })
      })
      .catch((error) => {
        console.log(error);
      });
  };

  updateCurrentComment(item) {
    this.setState({
      currentComment: item,
    })
  }

  render() {
    return (
      <div className="container-fluid">
        <div className="row">
          <div className="col s12">menu</div>
        </div>
        <div className="row">
          <div className="col s3"><CommentList comments={this.state.comments}
            updateCurrentComment={this.updateCurrentComment} />
          </div>
          <div className="col s9"><CommentSingle /></div>
        </div>
        <div className="row">
          <div className="col s12"><CommentForm /></div>
        </div>
      </div>
    );
  }
}

export default App;
